package com.xiaopengwei.admin;import de.codecentric.boot.admin.config.EnableAdminServer;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.context.annotation.Configuration;/** * @author Xiao pengwei * @since 2019-04-02 */@Configuration@EnableAutoConfiguration@EnableAdminServerpublic class SpringBootAdminApplication {    public static void main(String[] args) {        SpringApplication.run(SpringBootAdminApplication.class, args);    }}